import React, { Component } from "react";
import {
  listTransactions,
  createTransaction,
  deleteTransaction
} from "./transactions";

class TransactionsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputDescription: "",
      inputAmount: 0,
      transactions: [],
      amount: 0
    };
  }

  async componentDidMount() {
    const transactions = await listTransactions();
    this.setState({ transactions });
  }

  onInputChange = event => {
    const inputDescription = event.target.value;
    this.setState({ inputDescription });
  };

  onInputAmountChange = event => {
    const inputAmount = event.target.value;
    this.setState({ inputAmount });
  };

  onInputKeyPress = async (event) => {
    if (event.nativeEvent.keyCode === 13) {
      await createTransaction( this.state.inputDescription, this.state.inputAmount );
      const transactions = await listTransactions();
        this.setState({ transactions, inputDescription: "", inputAmount: 0 });
    }
  };




  render() {
    return (
      <div className="container">
        <form>
          <div>
            <select className="type" onChange={this.onDropDown}>
              <option value="income">+</option>
                <option value="expense">-</option>

            </select>
          </div>

          <input
            className="input"
            value={this.setState.inputDescription}
            onChange={this.onInputChange}
            placeholder="Description"
          />

          <input
            className="input"
            type="number"
            value={this.setState.inputAmount}
            onChange={this.onInputAmountChange}
            onKeyPress={this.onInputKeyPress}
            placeholder="Amount"
          />
        </form>

        {this.state.transactions.map(transaction => {
          return (
            <label key={transaction.id} className="transaction">
              {transaction.title}
                  {transaction.amount}

            </label>
          );
        })}
      </div>
    );
  }
}

export default TransactionsList;
