import React, { Component } from "react";
import {
  listTransactions,
  createTransaction,
  deleteTransaction
} from "./transactions";

class TransactionsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputDescription: "",
      inputAmount: 0,
      transactions: [],
      amount: 0
    };
  }

  async componentDidMount() {
    const transactions = await listTransactions();
    this.setState({ transactions });
  }

    onInputChange = (event) => {
        const inputDescription = event.target.value;
        this.setState({ inputDescription })

        const inputAmount = event.target.value;
        this.setState({inputAmount})
   } 

  render() {
    return (
      <div>
        <form>
          <label>Transaction description</label>
          <input className="input" value={this.setState.inputDescription} onChange={this.onInputChange} />
          <label>Amount</label>
          <input className="input" type="number" value={this.setState.inputAmount} onChange={this.onInputChange} onKeyPress />
        </form>

        {this.state.transactions.map(transaction => {
          return (
            <label key={transaction.id} className="transaction">
              {transaction.title}
            </label>
          );
        })}
      </div>
    );
  }
}

export default TransactionsList;
