import React, { Component } from "react";
import {
  listTransactions,
  createTransaction,
  deleteTransaction
} from "./transactions";

class TransactionsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputDescription: "",
      inputAmount: 0,
      transactions: [],
      amount: 0
    };
  }

  async componentDidMount() {
    const transactions = await listTransactions();
    this.setState({ transactions });
  }

  render() {
    return (
      <div>
        <form>
          <label>Transaction description</label>
          <input className="input" value={this.setState.inputDescription} />
          <label>Amount</label>
          <input className="input" type="number" value={this.setState.inputAmount} />
        </form>

        {this.state.transactions.map(transaction => {
          return (
            <label key={transaction.id} className="transaction">
              {transaction.title}
            </label>
          );
        })}
      </div>
    );
  }
}

export default TransactionsList;
